import axios from "axios";
const baseURL = process.env.BASEURL || 'https://dummyjson.com';

const getAllProducts = () => {
    const response = axios.get(`${baseURL}/products`);
    return response;
};

const getProduct = (id: number) => {
    axios.get(`${baseURL}/product/${id}`)
    .then(response => {
        return response;
    });
}

const searchProducts = (productInfo: any) => {
    axios.get(`${baseURL}/products/search?q=${productInfo}`)
    .then(response => {
        return response;
    })
}

export default getAllProducts;
